<?php$GLOBALS['URL'] = 'localhost/arthurvieira/sandbox/sitebeta/';function getProtocol() {	if (isset($_SERVER['HTTPS'])) {		return $_SERVER['HTTPS'] == 'on' ? 'https://' : 'http://';	} else {		return 'http://';	}}function getCurrentURL() {	$pageURL = getProtocol();		$pageURL .= $_SERVER['SERVER_PORT'] != '80' ? $_SERVER["SERVER_NAME"].":".$_SERVER["SERVER_PORT"].$_SERVER["REQUEST_URI"] : $_SERVER['SERVER_NAME'] . $_SERVER['REQUEST_URI'];	return $pageURL;}function getBaseURL() {	return getProtocol().$GLOBALS['URL'];}?>